def green_chases_rabbit_then_not_eat_mouse(x):
    return Implies(And(green(x), chases(x, rabbit)), Not(eats(mouse, rabbit))

# If something is green then it visits the dog.
def green_then_visits_dog(x):
    return Implies(green(x), visits(x, dog))

# If something visits the rabbit and the rabbit is young then it is round.
def visits_rabbit_young_then_round(x):
    return Implies(And(visits(x, rabbit), young(rabbit)), round(x))

# If something is round and it visits the dog then it chases the tiger.
def round_then_visits_dog_chases_tiger(x):
    return Implies(And(round(x), visits(x, dog)), chases(x, tiger))

# If something is big then it visits the rabbit.
def big_then_visits_rabbit(x):
